function simplerwc_button_separator_template()
{
    return '<span class="simpler-separator">OR</span>';
}

function simplerwc_legacy_maybe_render_sale_ribbon()
{
    $shouldRender = \get_option('simplerwc_should_render_sale_ribbon');
    $ribbonText = \get_option('simplerwc_sale_ribbon_text');
    if ($shouldRender && strlen($ribbonText) > 0) {
        return '<div class="simpler-ribbon"><span>' . $ribbonText . '</span><span class="simpler-ribbon-addon">OFF</span></div>';
    }
    return '';
}

function simplerwc_accepted_cards_notice_template()
{
    $html = '<div class="simpler-accepted-cards"><span>Pay With</span>';
    $html .= simplerwc_visa_icon(32, 24);
    $html .= simplerwc_mastercard_icon(32, 24);
    $html .= simplerwc_amex_icon(32, 24);
    $html .= '</div>';
    return $html;
}

function simplerwc_mastercard_icon($width, $height)
{
    return '<img width="' . $width . '" height="' . $height . '" alt="Mastercard" style="width:' . $width . 'px !important; height:' . $height . 'px !important;"
    src=" data:image/png;base64,"
    />';
}

function simplerwc_visa_icon($width, $height)
{
    return '<img width="' . $width . '" height="' . $height . '" alt="VISA" style="width:' . $width . 'px !important; height:' . $height . 'px !important;"
    src="data:image/png;base64,"
    />';
}

function simplerwc_amex_icon($width, $height)
{
    return '<img width="' . $width . '" height="' . $height . '" alt="AMEX" style="width:' . $width . 'px !important; height:' . $height . 'px !important;"
    src=" data:image/png;base64,"
    />';
}
